mapper.select:
select * 
from tbl_member
where member_id = #{id}
and member_pw = #{pw}

mapper.update:
UPDATE tbl_member

	
		member_name = #{memberName}
	
	
		, member_phone = #{memberPhone}
	
	
		, member_email = #{memberEmail}
	
	
		, member_pw = #{memberPw}
	

WHERE member_id = #{memberId}
