mapper.select:
select * 
from tbl_member
where member_id = #{id}
and member_pw = #{pw}

mapper.update:
UPDATE tbl_member

	
		member_name = #{memberName}
	
	
		, member_phone = #{memberPhone}
	
	
		, member_email = #{memberEmail}
	
	
		, member_pw = #{memberPw}
	

WHERE member_id = #{memberId}

update tbl_member 
set login_date = sysdate
where member_id = #{memberId}

update tbl_member 
set member_pw = #{pw}
where member_email = #{email}

update tbl_member 
set verification_code = #{code}
where member_id = #{id}
