mapper.select:
SELECT p.product_no ,p.category_no ,p.product_name ,p.product_price ,p.product_img ,p.product_thumbnail_img ,p.product_content ,p.reg_date ,c.cd_name FROM tbl_product p JOIN tbl_category c ON p.category_no = c.category_no WHERE 1=1
AND p.product_name LIKE '%' || #{keyword} || '%'
AND c.cd_name = #{cdName}
